		SET @parentIDlist=left(@parentIDlist,(len(@parentIDlist)-1))
	if left(@parentIDlist,1) = @deliminator
		SET @parentIDlist=right(@parentIDlist,(len(@parentIDlist)-1))

--print 'list: ' + @parentIDlist

select @parentIDlist as parentIDlist

RETURN
